#!/bin/bashPROGNAME=$0usage() {  cat << EOF >&2Usage: $PROGNAME [-lang <lang>] [-o <folder>] [-w <wikidata>]-lang <lang>: The language code-o <folder>: Output folder-w <wikidata>: Specify if you want to download wikidata or not. Default: trueEOF  exit 1}lang='en' folder='' wikidata=truewhile getopts lang:o:w: o; do  case $o in    (lang) lang=$OPTARG;;    (o) folder=$OPTARG;;    (w) wikidata=$OPTARG;;    (*) usage  esacdoneshift "$((OPTIND - 1))"LATEST_WIKIMEDIA_URL="https://dumps.wikimedia.org/"${lang}"wiki/latest/"echo "Downloading dumps TensorFlow Records..."if $# = 2 -a ${wikidata} = true;then    wget https://dumps.wikimedia.org/wikidatawiki/entities/latest-all.json.bz2 > ${folder}latest-all.json.bz2    tar xvjf ${folder}latest-all.json.bz2fiwget ${LATEST_WIKIMEDIA_URL}/${lang}wiki-latest-pages-articles.xml.bz2 > ${folder}${lang}wiki-latest-pages-articles.xml.bz2wget ${LATEST_WIKIMEDIA_URL}/${lang}wiki-latest-page_props.sql.gz > ${folder}${lang}wiki-latest-page_props.sql.gztar xvzf ${folder}${lang}wiki-latest-page_props.sql.gzecho "Done."